<?php
$imagem = "";
$imagem_padrao = "data:image/png;base64,";
$img_id = $this->input->get('img_id');

$imagens = $this->db->where('img_id', $img_id)
        ->get('imagem_produto')
        ->row();

if (count($imagens) > 0) {
   
        $imagem = base_url("/public/imagem/uploads-produtos/{$imagens->img_nome}");

        if (!@file_get_contents($imagem)) {
            $imagem = $imagem_padrao;
        }
        list($width, $height, $type, $attr) = getimagesize($imagem);
        echo "<img class='imagem_ajustar' style='cursor: pointer;' src='" . $imagem . "' height='{$height}' width='".($width-30)."' />";

    echo "<script>   $('#myModalImagem').css('width',{$width});
                        $('#myModalImagem').css('margin-left', -(".($width/2)."));
                        $('#myModalImagem').css('top', 10);
                        $('#myModalImagem > .modal-body').css('max-height',{$height});
                       $('#myModalImagem > .modal-body').css('width',".($width-10).");
         </script>";
    
}
?>

