<?php class ControllerChatChat extends Controller {	public function index() {		$this->data['title'] = $this->document->getTitle(); 		   $this->load->model('chat/chat');	   $rs_conversa = $this->model_chat_chat->conversas();	   $this->data['conversas'] = $rs_conversa->rows;	   $this->data['num_rows'] = $rs_conversa->num_rows;	   	   	   $this->children = array(			'common/header',			'common/footer'		);	   	  $this->template = 'chat/chat.tpl'; 	  $this->response->setOutput($this->render());		}			public function conversa(){		$this->load->model('chat/chat');	    $rs_conversa = $this->model_chat_chat->get_conversa($this->request->get['ch_id']);		$this->data['conversa'] = $rs_conversa->row;		}}?>