<?php class ModelSettignBodyLayout extends Model {	public function getBodyLayout(){            $query = $this->db->query("SELECT * FROM loja_body_layout");		            return $query;	}		public function editBodyLayout($data) {           //Verfiica se foi gravado o registro unico            $registroUnico = $this->db->query("SELECT * FROM loja_body_layout")->row;                           //INSERE REGISTRO SE NÃO TIVER REGITRO GRAVADO            if(count($registroUnico)==0){              $this->db->query("INSERT INTO " . DB_PREFIX . "body_layout SET bl_id = '" . (int)$data['id'] . "', `bl_image` = '" . $data['image'] . "', `bl_cor` = '" . $data['cor'] . "', `bl_repeat` = '" . $data['repetir']  . "', `bl_position` = '" . $data['posicao']  . "'");            }                        //Update            $this->db->query("UPDATE " . DB_PREFIX . "body_layout SET bl_id = '" . (int)$data['id'] . "', `bl_image` = '" . $data['image'] . "', `bl_cor` = '" . $data['cor'] . "', `bl_repeat` = '" . $data['repetir']  . "', `bl_position` = '" . $data['posicao']  . "'");	}}?>